  {
    "type": "theorem",
    "name": "Theorem 1",
    "statement": "If f: ℝ → ℝ is a continuous function such that f''(x) > 0 for all x ∈ ℝ, then f is unbounded.",
    "status": "proved",
    "proof": [
      {
        "type": "assumption",
        "statement": "Assume the contrary, that f is bounded."
      },
      {
        "type": "let",
        "variable": "(m, M)",
        "value": "There exist m, M ∈ ℝ such that m ≤ f(x) ≤ M for all x ∈ ℝ."
      },
      {
        "type": "observation",
        "statement": "Since f''(x) > 0 for all x, f is strictly concave up. Therefore, f has at most one local minimum."
      },
      {
        "type": "split_by",
        "term": "f",
        "cases": [
          {
            "case": "f has a local minimum at x=c",
            "content": [
              {
                "type": "let",
                "variable": "c",
                "value": "f has a local minimum at x=c. Then f(x) ≥ f(c) for all x ∈ ℝ."
              },
              {
                "type": "observation",
                "statement": "Because f is bounded, f(x) ≤ M for all x ∈ ℝ, and f(c) ≤ M."
              },
              {
                "type": "assertion",
                "claim": "But as f is strictly concave up, f(x) tends to infinity as x tends to both -∞ and +∞, contradicting the assumption that f is bounded above."
              }
            ]
          },
          {
            "case": "f has no local minimum",
            "content": [
              {
                "type": "observation",
                "statement": "f(x) is strictly decreasing or strictly increasing for all x ∈ ℝ."
              },
              {
                "type": "split_by",
                "term": "f",
                "cases": [
                  {
                    "case": "f is strictly decreasing",
                    "content": [
                      {
                        "type": "assertion",
                        "claim": "Then f(x) tends to negative infinity as x tends to +∞, contradicting the assumption that f is bounded below."
                      }
                    ]
                  },
                  {
                    "case": "f is strictly increasing",
                    "content": [
                      {
                        "type": "assertion",
                        "claim": "In this case, f(x) tends to positive infinity as x tends to +∞, which contradicts the assumption that f is bounded above."
                      }
                    ]
                  }
                ]
              }
            ]
          }
        ]
      },
      {
        "type": "conclusion",
        "content": "By contradiction, f must be unbounded."
      }
    ]
  }
]
