{
  "Theorem_1": {
    "Statement": "Let A be a square matrix. If A^3 = I, then A is diagonalizable.",
    "Proof": "The polynomial p(x) = x^3 - 1 annihilates A, and its roots are simple: 1, ω, ω^2 where ω is a primitive third root of unity. The minimal polynomial of A divides p(x) and thus all of its roots are also simple. Therefore, A is diagonalizable."
  },
  "Theorem_2": {
    "Statement": "The polynomial p(x) = x^3 - 1 has simple roots.",
    "Proof": "The roots of the polynomial p(x) = x^3 - 1 are 1, ω, ω^2 where ω is a primitive third root of unity. Since these roots are distinct, they are simple."
  },
  "Theorem_3": {
    "Statement": "If the minimal polynomial of a square matrix A divides a polynomial p(x), then all roots of the minimal polynomial are also roots of p(x).",
    "Proof": "Since the minimal polynomial of A divides p(x), it can be written as a product of factors that are also factors of p(x). Thus, every root of the minimal polynomial is also a root of p(x)."
  },
  "Theorem_4": {
    "Statement": "A square matrix A is diagonalizable if and only if the minimal polynomial of A has only simple roots.",
    "Proof": "If the minimal polynomial of A has only simple roots, then A is similar to a diagonal matrix, so A is diagonalizable. Conversely, if A is diagonalizable, then its minimal polynomial has only simple roots."
  }
