{
  "Theorem 1": {
    "Statement": "A continuous function $f: \\mathbb{R} \\to \\mathbb{R}$ that has $f'(x) > 0$ for all $x \\in \\mathbb{R}$ is strictly increasing.",
    "Proof": "Since $f'(x) > 0$ for all $x \\in \\mathbb{R}$, the function $f$ is strictly increasing by the monotonicity rule for differentiable functions."
  },
  "Theorem 2": {
    "Statement": "A strictly increasing function $f: \\mathbb{R} \\to \\mathbb{R}$ is unbounded.",
    "Proof": "By definition, a strictly increasing function $f$ becomes arbitrarily large as $x$ approaches positive infinity and arbitrarily small as $x$ approaches negative infinity. Hence $f$ is unbounded."
  }
}
