[
  {
    "type": "theorem",
    "name": "Matrix Invertibility Theorem",
    "statement": "If the square of a square matrix is invertible, then the matrix itself is also invertible.",
    "status": "proved",
    "proof": [
      {
        "type": "assumption",
        "statement": "Let A be a square matrix and B = A^2. B is invertible."
      },
      {
        "type": "definition",
        "statement": "There exists a matrix B^-1 such that BB^-1 = B^-1B = I",
        "term": "inverse matrix"
      },
      {
        "type": "assertion",
        "claim": "Substituting B = A^2 = AA into these equations results in AAB^-1 = B^-1AA = I",
        "deduced-from": ["B = A^2", "BB^-1 = B^-1B = I"],
        "justification": "substituting B with AA"
      },
      {
        "type": "let",
        "variable": "C",
        "value": "AB^-1",
        "justification": "defining C = AB^-1"
      },
      {
        "type": "assertion",
        "claim": "AC = CA = I",
        "deduced-from": ["C = AB^-1", "AAB^-1 = B^-1AA = I"],
        "justification": "multiplying A and C"
      },
      {
        "type": "observation",
        "statement": "C is the inverse of A"
      },
      {
        "type": "assertion",
        "claim": "A is invertible",
        "deduced-from": ["AC = CA = I"],
        "justification": "definition of invertibility"
      },
    ]
  }
]

