    "statement": "If A^2 is invertible for a square matrix A, then A is invertible.",
    "status": "proved",
    "proof": [
      {
        "type": "assumption",
        "statement": "A^2 is invertible."
      },
      {
        "type": "choose",
        "variable": "B",
        "property": "A^2B=BA^2=I, with I being the identity matrix.",
        "existence": [
          {
            "type": "observation",
            "statement": "Invertibility of A^2 implies existence of such a B."
          }
        ]
      },
      {
        "type": "assertion",
        "claim": "AB=A",
        "statement": "If A^2 is invertible and A^2B=I for some B, then AB=A.",
        "deduced-from": [
          {
            "result": "A^2B=I",
            "applied-to": "A"
          }
        ],
        "justification": "Multiply both sides of A^2B=I by A on the left, yielding A(A^2B)=(AA^2)B=A^3B. Since A^2B=I, we have A^3B=AI, which simplifies to A."
      },
      {
        "type": "assertion",
        "claim": "BA=B",
        "statement": "If A^2 is invertible and BA^2=I for some B, then BA=B.",
        "deduced-from": [
          {
            "result": "BA^2=I",
            "applied-to": "A"
          }
        ],
        "justification": "Multiply both sides of BA^2=I by A on the right, yielding B(AA^2)=(BA^2)A=B(A^3). Replacing A^2 with I again, we get B(A^3)=BI, which simplifies to B."
      },
      {
        "type": "observation",
        "statement": "A has a two-sided inverse B, hence A is invertible."
      }
    ]
  }
