    "type": "theorem",
    "name": "Infinitely many primes end with 7",
    "statement": "There are infinitely many primes with a last digit of 7.",
    "status": "proved",
    "proof": [
      {
        "type": "assumption",
        "statement": "Assume to the contrary that there are only finitely many such primes, say $p_1, p_2, ..., p_n$."
      },
      {
        "type": "let",
        "variable": "N",
        "value": "Construct a number $N = 10\cdot(p_1 p_2\cdots p_n) - 3$. The last digit of $N$ is 7."
      },
      {
        "type": "split_by",
        "condition": "Whether $N$ is prime or composite",
        "cases": [
          {
            "case": "$N$ is prime",
            "content": [
              {
                "type": "assertion",
                "claim": "$N$ is a new prime with the last digit 7, contradicting the assumption",
                "deduced-from": []
              }
            ]
          },
          {
            "case": "$N$ is composite",
            "content": [
              {
                "type": "choose",
                "variable": "p",
                "property": "$N$ has a prime divisor $p$"
              },
              {
                "type": "observation",
                "statement": "$N$ being odd and not divisible by 5 means $p$ cannot end with 2, 5, or 0. Therefore, $p$ must end with either 3, 7, or 9."
              },
              {
                "type": "assertion",
                "claim": "$p$ ends with 7",
                "deduced-from": [
                  {
                    "result": "$p$ cannot end with 3 or 9 because that would imply $N$ ends with 3 or 9, contradicting our construction of $N$"
                  }
                ]
              },
              {
                "type": "assertion",
                "claim": "$p$ is not one of $p_1, p_2, ..., p_n$",
                "deduced-from": [
                  {
                    "result": "For any $i$, $N \\equiv -3 \\mod p_i$, but also $N \\equiv 7 \\mod p_i$, a contradiction"
                  }
                ]
              },
              {
                "type": "assertion",
                "claim": "$p$ is a new prime that ends with 7, contradicting the assumption",
                "deduced-from": []
              }
            ]
          }
        ]
      }
    ]
  }
]

