      },
      {
        "type": "assertion",
        "claim": "A right inverse for $A$ exists, which is $C = AB$.",
        "deduced-from": [
          "$AC = I$"
        ]
      },
      {
        "type": "assertion",
        "claim": "$A$ also has a left inverse.",
