forall {X : Type u_1} {Y : Type u_2} [inst : TopologicalSpace X] [inst_1 : MetricSpace Y] {x : X} {f : X → Y} {f_n : ℕ → X → Y}
  {x_n : ℕ → X}, Filter.Tendsto x_n Filter.atTop (nhds x) → TendstoUniformly f_n f Filter.atTop →
    Filter.Tendsto (fun n => f_n n (x_n n)) Filter.atTop (nhds (f x))
